class ScrollAnimations{constructor(){this.observer = null;this.animatedElements = document.querySelectorAll('[data-animate]');}init(){const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion:reduce)').matches;if(prefersReducedMotion){this.animatedElements.forEach(el =>{el.classList.add('opacity-100','translate-y-0');});return;}if(!('IntersectionObserver' in window)){this.animatedElements.forEach(el =>{el.classList.add('opacity-100','translate-y-0');});return;}const options ={threshold:0.1,rootMargin:'0px 0px -50px 0px'};this.observer = new IntersectionObserver((entries)=>{entries.forEach(entry =>{if(entry.isIntersecting){entry.target.classList.add('opacity-100','translate-y-0');entry.target.classList.remove('opacity-0','translate-y-4');this.observer.unobserve(entry.target);}});},options);this.animatedElements.forEach(el =>{el.classList.add('opacity-0','translate-y-4','transition-all','duration-700');this.observer.observe(el);});}destroy(){if(this.observer){this.observer.disconnect();}}}window.ScrollAnimations = ScrollAnimations;if(document.readyState === 'loading'){document.addEventListener('DOMContentLoaded',()=>{const scrollAnimations = new ScrollAnimations();scrollAnimations.init();});}else{const scrollAnimations = new ScrollAnimations();scrollAnimations.init();}