class ProjectsSection{constructor(containerId,projectsData){this.container = document.getElementById(containerId);this.projectsData = projectsData || [];if(!this.container){console.error(`Container with ID "${containerId}" not found`);}}render(){if(!this.container){console.error('Cannot render:container not found');return;}this.container.innerHTML = '';if(!Array.isArray(this.projectsData)|| this.projectsData.length === 0){console.warn('No projects data to render');this.container.innerHTML = '<p class="text-center text-gray-600 col-span-full">プロジェクトがありません。</p>';return;}this.projectsData.forEach(projectData =>{if(!this.isValidProject(projectData)){console.warn('Skipping invalid project:',projectData);return;}const card = new ProjectCard(projectData);this.container.innerHTML += card.render();});console.log(`Rendered ${this.projectsData.length}project cards`);requestAnimationFrame(()=>{if(window.ScrollAnimations){const scrollAnimations = new window.ScrollAnimations();scrollAnimations.init();}});}isValidProject(project){return(project && typeof project.id === 'string' && typeof project.title === 'string' && typeof project.description === 'string' && typeof project.image === 'string');}}if(typeof module !== 'undefined' && module.exports){module.exports ={ProjectsSection};}